package Basics;

public class stringMethosExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name="John Kenedy";
		String Substr="john";
		
		System.out.println(name.toLowerCase().contains(Substr));
		
		System.out.println(name.replace("J","j").contains(Substr));



	}

}
